import React, { useState } from 'react';
import './App.css';

function App() {
  const [toDos, setToDos] = useState([]);
  const [toDo, setTodo] = useState('');
  const [scheduled, setScheduled] = useState([]);
  const [finished, setFinished] = useState([]);

  const handleCheck = (id, time) => {
    const updatedToDos = toDos.map((obj) => {
      if (obj.id === id) {
        obj.status = true;
        obj.time = time;
        setFinished([...finished, obj]);
        return null;
      }
      return obj;
    });
    setToDos(updatedToDos.filter(obj => obj !== null));
  };

  return (
    <div className="app">
      <div className='to-do-list'>
        <div className="mainHeading">
          <h1>ToDo List</h1>
        </div>
        <div className="subHeading">
          <br />
          <h2>Whoop, it's {new Date().toLocaleDateString('en-US', { weekday: 'long' })} üåù ‚òï </h2>

        </div>
        <div className="input">
  <input value={toDo} onChange={(e) => setTodo(e.target.value)} type="text" placeholder="üñäÔ∏è Add item..." />
  <i onClick={() => {
      const currentTime = new Date().toLocaleString();
      setToDos([...toDos, { id: Date.now(), text: toDo, status: false, time: currentTime }])
    }} className="fas fa-plus"></i>
</div>

      </div>

      <div className="lists-container">


        {/* Finished List */}
        <section className="finished-list">
          <div className="container">
            <div className="finished-list-border">
              <h1>Finished List</h1>
              <div className="todos">
                {finished.map((obj) => {
                  return (
                    <div className="todo" key={obj.id}>
                      <div className="left">
                        <p>{obj.text}</p>
                        <p>{obj.time}</p>

                      </div>
                    </div>
                  )
                })}
              </div>
            </div>
          </div>
        </section>

        {/* Progress List */}
        <section className="progress-list">
          <div className="container">
            <div className="progress-list-border">
              <h1>Progress List</h1>
              <div className="todos">
                {toDos.map((obj) => {
                  return (
                    <div className="todo" key={obj.id}>
                      <div className="left">
                        <input
                          onChange={() => handleCheck(obj.id, new Date().toLocaleString())}
                          value={obj.status}
                          type="checkbox"
                          name=""
                          id=""

                        />
                        <p>{obj.text}</p>
                        {obj.time && <p>Clicked at {obj.time}</p>}
                      </div>
                      <div className="right">
                        <i onClick={() => {
                          setToDos(toDos.filter(obj2 => obj2.id !== obj.id));
                          setScheduled([...scheduled, { id: Date.now(), text: obj.text, status: false, time: new Date().toLocaleString() }]);

                        }} className="fas fa-times"></i>
                      </div>
                    </div>
                  )
                })}
              </div>
            </div>
          </div>
        </section>

        {/* Scheduled List */}
        <section className="scheduled-list">
          <div className="container">
            <div className="scheduled-list-border">
              <h1>Scheduled List</h1>
              <div className="todos">
                {scheduled.map((obj) => {
                  return (
                    <div className="todo" key={obj.id}>
                      <div className="left">
                        <p>{obj.text}</p>
                        <p>{obj.time}</p>
                      </div>
                    </div>
                  )
                })}
              </div>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
}

export default App;
