import React, { useState } from "react";
import "./App.css";

function App() {
  const [toDos, setToDos] = useState([]);
  const [toDo, setTodo] = useState("");
  const [scheduled, setScheduled] = useState([]);

  const handleCheckboxChange = (id) => {
    setToDos((prevToDos) =>
      prevToDos.map((todo) => {
        if (todo.id === id) {
          return { ...todo, status: true };
        }
        return todo;
      })
    );
  };

  const handleDelete = (id) => {
    const deletedTodo = toDos.find((todo) => todo.id === id);
    setToDos((prevToDos) => prevToDos.filter((todo) => todo.id !== id));
    setScheduled((prevScheduled) => [...prevScheduled, deletedTodo]);
  };

  return (
    <div className="app">
      <div className="to-do-list">
        <div className="mainHeading">
          <h1>ToDo List</h1>
        </div>
        <div className="subHeading">
          <br />
          <h2>Whoop, it's Wednesday 🌝 ☕ </h2>
        </div>
        <div className="input">
          <input
            value={toDo}
            onChange={(e) => setTodo(e.target.value)}
            type="text"
            placeholder="🖊️ Add item..."
          />
          <i
            onClick={() =>
              setToDos([
                ...toDos,
                { id: Date.now(), text: toDo, status: false },
              ])
            }
            className="fas fa-plus"
          ></i>
        </div>
      </div>
      <div className="lists-container">
        {/* Finished list */}
        <section className="finished-list">
          <div className="container">
            <div className="finished-list-border">
              <h1>Finished List</h1>
              {toDos.map((obj) => {
                if (obj.status) {
                  return <h1>{obj.text}</h1>;
                }
                return null;
              })}
            </div>
          </div>
        </section>

        {/* Progress list */}
        <section className="progress-list">
          <div className="container">
            <div className="progress-list-border">
              <h1>Progress List</h1>

              <div className="todos">
                {toDos.map((obj) => (
                  <div className="todo" key={obj.id}>
                    <div className="left">
                      <input
                        onChange={() => handleCheckboxChange(obj.id)}
                        checked={obj.status}
                        type="checkbox"
                      />
                      <p>{obj.text}</p>
                    </div>
                    <div className="right">
                      <i
                        onClick={() => handleDelete(obj.id)}
                        className="fas fa-times"
                      ></i>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </section>

        {/* Scheduled list */}
        <section className="scheduled-list">
          <div className="container">
            <div className="scheduled-list-border">
              <h1>Scheduled List</h1>
              {scheduled.map((obj) => (
                <h1 key={obj.id}>{obj.text}</h1>
              ))}
            </div>
          </div>
        </section>
      </div>
    </div>
  );
}

export default App;
