import React from 'react';
import './App.css';
import { useState } from 'react';

function App() {
  const [toDos, setToDos] = useState([]);
  const [toDo, setTodo] = useState('');

  return (
    <div className="app">
      <div className="mainHeading">
        <h1>ToDo List</h1>
      </div>
      <div className="subHeading">
        <br />
        <h2>Whoop, it's Wednesday üåù ‚òï </h2>
      </div>
      <div className="halfWidth">
        <div className="input">
          <input
            value={toDo}
            onChange={(e) => setTodo(e.target.value)}
            type="text"
            placeholder="üñäÔ∏è Add item..."
          />
          <i
            onClick={() =>
              setToDos([
                ...toDos,
                { id: Date.now(), text: toDo, status: false },
              ])
            }
            className="fas fa-plus"
          ></i>
        </div>
        <div className="todos">
          {toDos.map((obj) => {
            return (
              <div className="todo">
                <div className="left">
                  <input
                    onChange={(e) => {
                      setToDos(
                        toDos.map((obj2) => {
                          if (obj2.id === obj.id) {
                            obj2.status = e.target.checked;
                          }
                          return obj2;
                        })
                      );
                    }}
                    checked={obj.status}
                    type="checkbox"
                    name=""
                    id=""
                  />
                  <p>{obj.text}</p>
                </div>
                <div className="right">
                  <i className="fas fa-times"></i>
                </div>
              </div>
            );
          })}
          {toDos.map((obj) => {
            if (obj.status) {
              return <h1>{obj.text}</h1>;
            }
            return null;
          })}
        </div>
      </div>
      <div className="equalWidth">
        <section className="finished-list">
          <div className="container">
            <div className="finished-list-border">
              <h1>Finished List</h1>
            </div>
          </div>
        </section>
        <section className="progress-list">
          <div className="container">
            <div className="progress-list-border">
              <h1>Progress List</h1>
            </div>
          </div>
        </section>
        <section className="scheduled-list">
          <div className="container">
            <div className="scheduled-list-border">
              <h1>Scheduled List</h1>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
}

export default App;
